mapper.select:
SELECT ticket.number, ticket.ticket_time ticketTime, ticket.product_id productId, ticket.session_id sessionId, ticket.line_id lineId, ticket.price, ticket.sell_price sellPrice, ticket.count, ticket.reservation, ticket.user_address_id userAddressId, ticket.NAME, ticket.tel, ticket.address, ticket.address_info addressInfo, ticket.shop_id shopId, ticket.shop_name shopName, ticket.shop_address shopAddress, ticket.ticket_form ticketForm, ticket.ticket_nonsupport ticketNonsupport, ticket.ticket_invoice ticketInvoice, ticket.ticket_expired ticketExpired, ticket.ticket_any_time ticketAnyTime, ticket.ticket_choose_seat ticketChooseSeat, ticket.ticket_post_way ticketPostWay, ticket.ticket_postage ticketPostage, ticket.logistics logistics, ticket.logistics_status logisticsStatus, ticket.logistics_com logisticsCom, ticket.product_name productName, ticket.session_name sessionName, ticket.line_name lineName, o.`status` FROM t_order_ticket ticket LEFT JOIN t_order o ON ticket.number = o.number where ticket.del_flag = '0'
AND ticket.number = #{bo.number}
AND ticket.product_name = #{bo.productName}
AND ticket.logistics_status = #{bo.logisticsStatus}
AND ticket.session_name = #{bo.sessionName}
AND ticket.line_name = #{bo.lineName}
AND o.status = #{bo.status}
AND ticket.create_time >= STR_TO_DATE(#{bo.params.beginCreateTime},'%Y-%m-%d %H:%i:%s')
AND ticket.create_time <= STR_TO_DATE(#{bo.params.endCreateTime},'%Y-%m-%d %H:%i:%s')
ORDER BY ticket.create_time DESC

